package com.dormain.ch03;

public class DenyLogicOperatorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		boolean play = true;
		
		System.out.println(play); // syso + ctrl + spacebar
		
		play = !play;
		System.out.println(play);
		
		play = !play;
		System.out.println(play);
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
