package com.dormain.ch04;

public class WhilePrintFrom1To10Example {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
			int i = 1;
			int sum = 0;
			while(i<=10) {
					System.out.println("합	계: " + sum);
					
					i++;
					sum += i;
				}
					//	int sum += i;
		
		
		
		
		
		
		
	}		

}
